import React from 'react';
import getItems from '../services/getItems'

export default function SearchResults({keyword}){
    const [products, setProducts] = useState([]);

    useEffect(function(){ 
      getItems({keyword}).then(products => setProducts(products))
    }, [keyword]);

    return <div className="container container-search-results">
        {
            products.map(item => {
                <>
                <div className="columns colums-search-results" onClick={() => alert("Clicked!")}>
                    <div className="column is-2">
                        <img className="custom-img-results" src="http://http2.mlstatic.com/D_651180-MLA42227826734_062020-I.jpg"></img>
                    </div>
                    <div className="column is-7">
                        <p className="price-search-results">
                            <span>$1990<sup>99</sup></span>
                            <img src="../assets/ic_shipping.png"></img>
                        </p>
                        <p className="title-search-results">esta es una desripción de la publicación esta es una desripción de la publicación esta es una desripción de la publicación</p>
                    </div>
                    <div className="column is-3 location-search-results">
                        <p>Capital Federal</p>
                    </div>
                </div>
                <hr className="hr-search-results"/>
                </>
            })
        }
      </div>
}
