import React from 'react';
import SearchResults from '../../components/SearchResults';
import { useLocation } from 'react-router-dom';
import {Helmet} from 'react-helmet';
import Header from '../../components/Header';

export default function SearchResult(){

    let urlParams = new URLSearchParams(useLocation().search);
    let query = urlParams.get("search");

    function capitalizeFirstLetter(string) {
        return string[0].toUpperCase() + string.slice(1);
    }

    return(
        <>
        <Helmet>
            <title>{ `${capitalizeFirstLetter(query)} | MeLi Challenge`}</title>
            <meta name="description" content={`Encontrá ${capitalizeFirstLetter(query)} en MeLi Challenge! Entrá y conocé nuestras increíbles ofertas y promociones. Descubrí la mejor forma de comprar online.`}/>
        </Helmet>
        <Header/>
        <SearchResults query={query}/>
        </>
    )
}
