import React, {useState} from 'react';

const [query, setQuery] = useState([]);

handleSearchSubmit: e => {
    if (e.keyCode === 13) {
        e.preventDefault();
        this.handleSearchSubmit(e);
      }
}

handleSearchSubmit: e => {
    e.preventDefault();
    setQuery(query);
    this.props.onQueryEnter(query);
}

export default function SearchBox(){
    const {query} = this.query;
    return <form className="app-header" onSubmit={handleSearchSubmit}>
        <div className="container">
            <div className="columns">
                <div className="column is-1 is-mobile">
                    <img className="img-logo" text={query} onKeyUp={handleKeyUp} alt="Mercado Libre" src="../assets/Logo_ML.png"></img>
                </div>
                <div className="column is-11 is-mobile">
                    <div className="field has-addons">
                        <div className="control custom-control">
                            <input className="input" type="text" placeholder="Nunca dejes de buscar"/>
                        </div>
                        <div className="control">
                            <input className="button bg-gray" type="submit">
                                <img className="img-logo" src="../assets/ic_Search.png"></img>
                            </input>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      </form>
}
