import rootUrl from "./rootUrl";
const fetch = require("node-fetch");

export default function getItemDetail(id){
    console.log(id);
    const url = rootUrl() + `items/${id}`;
    return fetch(url)
        .then(res => res.json())
        .then(response => { 
            return response;
        })
        .catch(err => console.log(err));
}
