import rootUrl from "./rootUrl";
const fetch = require("node-fetch");

export default function getItemDetail(id){
    const url = rootUrl() + `items/${id}`;
    return fetch(url)
        .then(response => { 
            if(response.ok){
                console.log(response);
                return response.json();
            }
            else{
                //return badre
            }

        })
        .catch(err => console.log(err));
}
