import rootUrl from "./rootUrl";
const fetch = require("node-fetch");

export default function getItemDetail(id){
    const url = rootUrl() + `items/${id}`;
    return fetch(url)
        .then(res => res.json())
        .then(response => { 
            console.log(response);
            if(response.status === 200){
                return response;
            }
        })
        .catch(err => console.log(err));
}
