import rootUrl from "./rootUrl";
const fetch = require("node-fetch");

export default function getItems({query = 'ipad'} = {}){


    const url = rootUrl("dev") + `items?q=${query}`;
    return fetch(url)
        //.then(res => res.json())
        .then(res => { 
            const {data = []} = res.items;
            // if(Array.isArray(data)){
            //     const products = data.
            // }
            console.log(data);
            return data;
        })
        .catch(err => console.log(err));
}
