import React from 'react';
import SearchResults from '../../components/SearchResults';
import { useLocation } from 'react-router-dom';
import {Helmet} from 'react-helmet';
import Header from '../../components/Header';

export default function SearchResult(){

    let urlParams = new URLSearchParams(useLocation().search);
    let query = urlParams.get("search");

    return(
        <>
        <Helmet>
            <title>{ `${query} - Resultados de búsqueda | MeLi Challenge`}</title>
            <meta name="description" content="Search results for MeLi Challenge"/>
        </Helmet>
        <Header/>
        <SearchResults query={query}/>
        </>
    )
}
