import React, {useEffect, useState} from 'react';
import getItems from '../services/getItems'

export default function SearchResults({keyword}){
    const [products, setProducts] = useState([]);

    useEffect(function(){ 
      getItems({keyword}).then(products => { setProducts(products); console.log(products) })
    }, [keyword]);

    return  products.map(prod => 
                <section key={prod.id} className="container container-search-results">
                    <div className="columns colums-search-results" onClick={() => alert("Clicked!")}>
                        <div className="column is-2">
                            <img className="custom-img-results" alt={prod.title} src={prod.picture}></img>
                        </div>
                        <div className="column is-7">
                            <p className="price-search-results">
                                <span>{prod.price.amount}<sup>{(prod.price.decimals * 100).toFixed()}</sup></span>
                                { prod.free_shipping ? <img alt="EnvÃ­o gratuito" src="../assets/ic_shipping.png"></img> : ''}
                            </p>
                            <p className="title-search-results">{prod.title}</p>
                        </div>
                        <div className="column is-3 location-search-results">
                            <p>{prod.location}</p>
                        </div>
                    </div>
                    <hr className="hr-search-results"/>
                </section>
        )
}
