import React, { useEffect, useState} from 'react';
import getItemDetail from '../services/getItemDetail'
import {useParams} from 'react-router-dom';
import Breadcrumbs from './Breadcrumbs';

export default function ProductDetail({params}){
    let {id} = useParams();
    const [product, setProduct] = useState('');
    useEffect(function(){ 
        getItemDetail({id}).then(product => setProduct(product))
    }, [id]);

    return <>
    <Breadcrumbs />
    <div className="container container-product-details">
        { 
        product.map(prod => {
            <>
            <div className="columns">
                <div className="column is-8 column-img-detail"> 
                    <img className="custom-img-detail" alt={prod.item.title} src="http://http2.mlstatic.com/D_719147-MLA44099619310_112020-O.jpg"></img>
                </div>    
                
                <div className="column is-8 column-img-detail"> 
                    <img className="custom-img-detail" alt={prod.item.title} src="http://http2.mlstatic.com/D_719147-MLA44099619310_112020-O.jpg"></img>
                </div>
                <div className="column is-4">
                    <section className="section">
                        <p className="product-detail-condition">{prod.item.condition = 'new' ? 'Nuevo' : 'Usado'} - {prod.item.sold_quantity} vendidos</p>
                        <p className="product-detail-title">{prod.item.title}</p>
                        <p className="product-detail-price">
                            <span>${prod.item.price.amount}<sup>{prod.item.price.decimals}</sup></span>
                        </p>
                        <div className="control product-detail-btn-container">
                            <button className="button is-link product-detail-btn">Comprar</button>
                        </div>
                    </section>
                </div>
            </div>
            <section className="section">
                <h1 className="product-description-title">Descripción del producto</h1>
            <p>{prod.item.description}</p> 
            </section>
            </>
        }
        )
    };
        </div>
    </>
}
